document.addEventListener("DOMContentLoaded",function(){document.querySelectorAll('input[name="name"]').forEach(e=>{e.addEventListener("input",function(){this.classList.contains("namedeals")||(this.value=this.value.replace(/[^А-Яа-яЁё\s\-]/g,""))})})});window.addEventListener("load",()=>{const t=document.getElementById("loading-screen"),e=document.getElementById("content");setTimeout(()=>{t.classList.add("hidden"),document.body.style.overflow="auto",setTimeout(()=>{t.style.display="none",e.style.opacity="1"},1e3)},1e3)});document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll("textarea").forEach(e=>{e.addEventListener("input",n=>{const o=/^[a-zA-Zа-яА-ЯёЁ0-9\s,.\-₽]*$/,a=n.target.value;o.test(a)||(n.target.value=a.replace(/[^a-zA-Zа-яА-ЯёЁ0-9\s,.\-₽]/g,""))})})});
